import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { RootState } from '../store';
import { updatePlayer } from '../store';
import { Character } from '../types';

const VALID_CODES = {
  'THOR2024': 'Thor',
  'IRONMAN3000': 'Iron Man',
  'STRANGE123': 'Dr. Strange',
  'CAP456': 'Captain America',
  'WONG789': 'Wong',
  'GROOT101': 'Groot',
  'LOKI666': 'Loki',
  'ROCKET999': 'Rocket',
  'PANTHER777': 'Black Panther',
  'LOGAN888': 'Wolverine',
  'POOL444': 'Deadpool',
  'HULK555': 'Hulk',
  'SPIDEY333': 'Spider-Man',
  'BUCKY222': 'Winter Soldier',
  'WIDOW111': 'Black Widow',
  'HAWKEYE777': 'Hawkeye',
  'MAKARI555': 'Makari',
  'ODIN999': 'Odin',
  'MYSTERIO666': 'Mysterio',
  'MARVEL444': 'Captain Marvel',
  'STARLORD333': 'Star Lord'
};

const RedeemCode: React.FC = () => {
  const [code, setCode] = useState('');
  const [message, setMessage] = useState<{ text: string; type: 'success' | 'error' } | null>(null);
  const dispatch = useDispatch();
  const { player } = useSelector((state: RootState) => state.game);

  const handleRedeem = () => {
    const upperCode = code.toUpperCase();
    if (VALID_CODES[upperCode as keyof typeof VALID_CODES]) {
      const characterName = VALID_CODES[upperCode as keyof typeof VALID_CODES];

      // Check if character is already unlocked
      const hasCharacter = player.characters.some(char => char.name === characterName);

      if (hasCharacter) {
        setMessage({
          text: `You already own ${characterName}!`,
          type: 'error'
        });
        return;
      }

      // Add character to player's collection with all required properties
      const newCharacter: Character = {
        id: characterName.toLowerCase().replace(' ', '-'),
        name: characterName,
        health: 1000,
        maxHealth: 1000,
        level: 1,
        experience: 0,
        isPaid: false,
        isUnlocked: true,
        abilities: [],
        skins: [],
        currentSkin: 'default'
      };

      dispatch(updatePlayer({
        characters: [...player.characters, newCharacter]
      }));

      setMessage({
        text: `Successfully redeemed ${characterName}!`,
        type: 'success'
      });
      setCode('');
    } else {
      setMessage({
        text: 'Invalid code. Please try again.',
        type: 'error'
      });
    }
  };

  return (
    <div className="min-h-screen bg-gray-900 p-8">
      <div className="max-w-md mx-auto">
        <h1 className="text-3xl font-bold mb-8">Redeem Code</h1>

        <div className="bg-gray-800 rounded-lg p-6 mb-6">
          <div className="mb-4">
            <label htmlFor="code" className="block text-sm font-medium mb-2">
              Enter your code
            </label>
            <input
              type="text"
              id="code"
              value={code}
              onChange={(e) => setCode(e.target.value)}
              className="w-full px-4 py-2 bg-gray-700 rounded-lg focus:ring-2 focus:ring-blue-500 outline-none"
              placeholder="Enter code here"
            />
          </div>
          <button
            onClick={handleRedeem}
            className="w-full btn-primary py-2"
          >
            Redeem
          </button>
        </div>

        {message && (
          <div
            className={`p-4 rounded-lg ${
              message.type === 'success' ? 'bg-green-800' : 'bg-red-800'
            }`}
          >
            {message.text}
          </div>
        )}

        <div className="mt-8">
          <h2 className="text-xl font-bold mb-4">Available Characters</h2>
          <div className="grid grid-cols-2 gap-4">
            {Object.values(VALID_CODES).map((character) => (
              <div
                key={character}
                className={`p-4 rounded-lg ${
                  player.characters.some((char) => char.name === character)
                    ? 'bg-green-800'
                    : 'bg-gray-800'
                }`}
              >
                <div className="font-bold">{character}</div>
                <div className="text-sm text-gray-400">
                  {player.characters.some((char) => char.name === character)
                    ? 'Unlocked'
                    : 'Locked'}
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default RedeemCode;
