import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { RootState } from '../store';

const Battle: React.FC = () => {
  const navigate = useNavigate();
  const { characters } = useSelector((state: RootState) => state.game);
  const [selectedTeam, setSelectedTeam] = useState<string[]>([]);

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      {/* Top Navigation */}
      <div className="bg-gray-800 p-4 flex justify-between items-center">
        <div className="flex items-center">
          <button 
            onClick={() => navigate('/')}
            className="mr-4 hover:text-gray-300"
          >
            <span className="material-icons">arrow_back</span>
          </button>
          <h1 className="text-2xl font-bold">Battle Arena</h1>
        </div>
        <div className="flex items-center space-x-4">
          <button className="bg-blue-600 px-4 py-2 rounded-lg hover:bg-blue-700">
            Quick Match
          </button>
          <button className="bg-purple-600 px-4 py-2 rounded-lg hover:bg-purple-700">
            Create Room
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div className="p-6">
        {/* Team Selection */}
        <div className="mb-8">
          <h2 className="text-xl font-bold mb-4">Select Your Team (3 Heroes)</h2>
          <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-4">
            {characters.map((character) => (
              <div 
                key={character.id}
                onClick={() => {
                  if (selectedTeam.includes(character.id)) {
                    setSelectedTeam(selectedTeam.filter(id => id !== character.id));
                  } else if (selectedTeam.length < 3) {
                    setSelectedTeam([...selectedTeam, character.id]);
                  }
                }}
                className={`
                  relative cursor-pointer rounded-lg overflow-hidden
                  ${selectedTeam.includes(character.id) ? 'ring-2 ring-yellow-500' : ''}
                  ${!character.isUnlocked ? 'opacity-50' : ''}
                `}
              >
                <div className="bg-gray-800 aspect-square flex items-center justify-center">
                  {/* Replace with actual character image */}
                  <span className="material-icons text-6xl">person</span>
                </div>
                <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-black p-2">
                  <p className="text-sm font-bold">{character.name}</p>
                  <p className="text-xs text-gray-300">Lv.{character.level}</p>
                </div>
                {!character.isUnlocked && (
                  <div className="absolute inset-0 bg-black bg-opacity-50 flex items-center justify-center">
                    <span className="material-icons">lock</span>
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>

        {/* Battle Modes */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          <div className="bg-gray-800 rounded-xl p-6 hover:bg-gray-700 cursor-pointer">
            <h3 className="text-xl font-bold mb-2">Ranked Match</h3>
            <p className="text-gray-400 mb-4">Compete for rankings and rewards</p>
            <div className="flex justify-between items-center">
              <span className="text-sm text-gray-300">Players: 324</span>
              <span className="material-icons text-yellow-500">star</span>
            </div>
          </div>

          <div className="bg-gray-800 rounded-xl p-6 hover:bg-gray-700 cursor-pointer">
            <h3 className="text-xl font-bold mb-2">Casual Match</h3>
            <p className="text-gray-400 mb-4">Practice and have fun</p>
            <div className="flex justify-between items-center">
              <span className="text-sm text-gray-300">Players: 156</span>
              <span className="material-icons text-blue-500">sports_esports</span>
            </div>
          </div>

          <div className="bg-gray-800 rounded-xl p-6 hover:bg-gray-700 cursor-pointer">
            <h3 className="text-xl font-bold mb-2">Tournament</h3>
            <p className="text-gray-400 mb-4">Join official tournaments</p>
            <div className="flex justify-between items-center">
              <span className="text-sm text-gray-300">Next: 2h 15m</span>
              <span className="material-icons text-purple-500">emoji_events</span>
            </div>
          </div>
        </div>
      </div>

      {/* Bottom Action Bar */}
      <div className="fixed bottom-0 left-0 right-0 bg-gray-800 p-4">
        <div className="container mx-auto flex justify-between items-center">
          <div className="flex items-center space-x-4">
            <p className="text-sm">Selected: {selectedTeam.length}/3</p>
            <div className="flex space-x-2">
              {selectedTeam.map((id) => (
                <div key={id} className="w-12 h-12 bg-gray-700 rounded-lg flex items-center justify-center">
                  <span className="material-icons">person</span>
                </div>
              ))}
              {Array(3 - selectedTeam.length).fill(null).map((_, i) => (
                <div key={i} className="w-12 h-12 bg-gray-700 rounded-lg flex items-center justify-center opacity-30">
                  <span className="material-icons">person_outline</span>
                </div>
              ))}
            </div>
          </div>
          <button 
            className={`
              px-8 py-3 rounded-lg text-xl font-bold
              ${selectedTeam.length === 3 
                ? 'bg-yellow-500 text-black hover:bg-yellow-400' 
                : 'bg-gray-700 text-gray-500 cursor-not-allowed'}
            `}
            disabled={selectedTeam.length !== 3}
          >
            FIGHT
          </button>
        </div>
      </div>
    </div>
  );
};

export default Battle;
