import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { RootState } from '../store';
import { updatePlayer, gainExperience } from '../store';
import styles from './Battle.module.css';

type Fighter = {
  id: string;
  name: string;
  health: number;
  maxHealth: number;
  isBlocking: boolean;
  isStunned: boolean;
};

const Battle: React.FC = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const { player, characters } = useSelector((state: RootState) => state.game);

  // Get the first selected character or default to Thor
  const playerCharacter = characters.find(c => c.id === player.selectedCharacters[0]) || 
                         characters.find(c => c.id === 'thor');

  // Random AI opponent
  const availableOpponents = characters.filter(c => c.isUnlocked);
  const randomOpponent = availableOpponents[Math.floor(Math.random() * availableOpponents.length)];

  const [playerFighter, setPlayerFighter] = useState<Fighter>({
    id: playerCharacter?.id || 'thor',
    name: playerCharacter?.name || 'Thor',
    health: playerCharacter?.health || 1000,
    maxHealth: playerCharacter?.maxHealth || 1000,
    isBlocking: false,
    isStunned: false
  });

  const [opponentFighter, setOpponentFighter] = useState<Fighter>({
    id: randomOpponent?.id || 'iron-man',
    name: randomOpponent?.name || 'Iron Man',
    health: randomOpponent?.health || 1000,
    maxHealth: randomOpponent?.maxHealth || 1000,
    isBlocking: false,
    isStunned: false
  });

  const [gameOver, setGameOver] = useState(false);
  const [winner, setWinner] = useState<string | null>(null);
  const [battleLog, setBattleLog] = useState<string[]>([]);

  const addToBattleLog = (message: string) => {
    setBattleLog(prev => [...prev, message].slice(-5)); // Keep last 5 messages
  };

  const performAttack = (damage: number, isPlayer: boolean) => {
    if (isPlayer) {
      if (!opponentFighter.isBlocking) {
        const newHealth = Math.max(0, opponentFighter.health - damage);
        setOpponentFighter(prev => ({ ...prev, health: newHealth }));
        addToBattleLog(`${playerFighter.name} deals ${damage} damage!`);
        if (newHealth === 0) {
          setGameOver(true);
          setWinner('player');
          dispatch(gainExperience(10)); // Award XP for winning
        }
      } else {
        addToBattleLog(`${opponentFighter.name} blocked the attack!`);
      }
    } else {
      if (!playerFighter.isBlocking) {
        const newHealth = Math.max(0, playerFighter.health - damage);
        setPlayerFighter(prev => ({ ...prev, health: newHealth }));
        addToBattleLog(`${opponentFighter.name} deals ${damage} damage!`);
        if (newHealth === 0) {
          setGameOver(true);
          setWinner('opponent');
        }
      } else {
        addToBattleLog(`${playerFighter.name} blocked the attack!`);
      }
    }
  };

  // AI opponent logic
  useEffect(() => {
    if (!gameOver && !opponentFighter.isStunned) {
      const aiInterval = setInterval(() => {
        const actions = ['attack', 'block', 'special'];
        const randomAction = actions[Math.floor(Math.random() * actions.length)];

        switch (randomAction) {
          case 'attack':
            performAttack(50, false);
            break;
          case 'block':
            setOpponentFighter(prev => ({ ...prev, isBlocking: true }));
            setTimeout(() => {
              setOpponentFighter(prev => ({ ...prev, isBlocking: false }));
            }, 2000);
            break;
          case 'special':
            performAttack(100, false);
            break;
        }
      }, 3000);

      return () => clearInterval(aiInterval);
    }
  }, [gameOver, opponentFighter.isStunned]);

  return (
    <div className="min-h-screen bg-gray-900 text-white relative">
      {/* Health Bars */}
      <div className="absolute top-0 left-0 right-0 p-4 z-10">
        <div className="flex justify-between items-center mb-4">
          {/* Player Health */}
          <div className="w-2/5">
            <div className="flex justify-between mb-1">
              <span className="font-bold">{playerFighter.name}</span>
              <span>{playerFighter.health}/{playerFighter.maxHealth}</span>
            </div>
            <div className="h-4 bg-gray-700 rounded-full overflow-hidden">
              <div 
                className="h-full bg-green-600 transition-all duration-300"
                style={{ width: `${(playerFighter.health / playerFighter.maxHealth) * 100}%` }}
              />
            </div>
          </div>

          {/* VS */}
          <div className="text-2xl font-bold text-yellow-500">VS</div>

          {/* Opponent Health */}
          <div className="w-2/5">
            <div className="flex justify-between mb-1">
              <span className="font-bold">{opponentFighter.name}</span>
              <span>{opponentFighter.health}/{opponentFighter.maxHealth}</span>
            </div>
            <div className="h-4 bg-gray-700 rounded-full overflow-hidden">
              <div 
                className="h-full bg-red-600 transition-all duration-300"
                style={{ width: `${(opponentFighter.health / opponentFighter.maxHealth) * 100}%` }}
              />
            </div>
          </div>
        </div>
      </div>

      {/* Battle Arena */}
      <div className={styles.arena}>
        <div className={styles.arenaFloor} />

        {/* Player Character */}
        <div className={`${styles.character} ${styles.characterLeft} 
          ${playerFighter.isBlocking ? styles.blocking : ''}
          ${playerFighter.isStunned ? styles.stunned : ''}`}
        >
          <div className={`${styles.characterModel} ${styles.playerModel}`}>
            <span className="material-icons text-8xl">person</span>
          </div>
        </div>

        {/* Opponent Character */}
        <div className={`${styles.character} ${styles.characterRight}
          ${opponentFighter.isBlocking ? styles.blocking : ''}
          ${opponentFighter.isStunned ? styles.stunned : ''}`}
        >
          <div className={`${styles.characterModel} ${styles.opponentModel}`}>
            <span className="material-icons text-8xl">person</span>
          </div>
        </div>

        {/* Arena Lighting */}
        <div className={styles.arenaLighting} />
      </div>

      {/* Battle Log */}
      <div className="absolute left-4 top-20 w-64 bg-gray-800 bg-opacity-90 rounded-lg p-4">
        <h3 className="font-bold mb-2">Battle Log</h3>
        <div className="space-y-1">
          {battleLog.map((log, index) => (
            <p key={index} className="text-sm text-gray-300">{log}</p>
          ))}
        </div>
      </div>

      {/* Action Buttons */}
      <div className="absolute bottom-0 left-0 right-0 p-6 bg-gray-800 bg-opacity-90">
        <div className="max-w-4xl mx-auto">
          <div className="grid grid-cols-3 gap-4">
            <div className="space-y-4">
              <button
                onClick={() => performAttack(50, true)}
                disabled={gameOver || playerFighter.isStunned}
                className="w-full bg-blue-600 px-6 py-3 rounded-lg hover:bg-blue-700 disabled:opacity-50"
              >
                Basic Attack
              </button>
              <button
                onClick={() => {
                  setPlayerFighter(prev => ({ ...prev, isBlocking: true }));
                  setTimeout(() => {
                    setPlayerFighter(prev => ({ ...prev, isBlocking: false }));
                  }, 2000);
                }}
                disabled={gameOver || playerFighter.isStunned}
                className="w-full bg-green-600 px-6 py-3 rounded-lg hover:bg-green-700 disabled:opacity-50"
              >
                Block
              </button>
            </div>

            <div>
              {playerCharacter?.abilities.map((ability) => (
                <button
                  key={ability.id}
                  onClick={() => performAttack(ability.damage, true)}
                  disabled={gameOver || playerFighter.isStunned}
                  className="w-full bg-purple-600 px-6 py-3 rounded-lg hover:bg-purple-700 disabled:opacity-50 mb-4"
                >
                  {ability.name}
                </button>
              ))}
            </div>

            <div className="text-right">
              <button
                onClick={() => navigate('/')}
                className="bg-red-600 px-6 py-3 rounded-lg hover:bg-red-700"
              >
                Surrender
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Game Over Overlay */}
      {gameOver && (
        <div className="absolute inset-0 bg-black bg-opacity-75 flex items-center justify-center">
          <div className="bg-gray-800 rounded-xl p-8 text-center">
            <h2 className="text-3xl font-bold mb-4">
              {winner === 'player' ? 'Victory!' : 'Defeat!'}
            </h2>
            <p className="mb-6">
              {winner === 'player' 
                ? 'Congratulations! You won the battle!' 
                : 'Better luck next time!'}
            </p>
            <div className="space-x-4">
              <button
                onClick={() => window.location.reload()}
                className="bg-blue-600 px-6 py-2 rounded-lg hover:bg-blue-700"
              >
                Fight Again
              </button>
              <button
                onClick={() => navigate('/')}
                className="bg-gray-600 px-6 py-2 rounded-lg hover:bg-gray-700"
              >
                Return to Lobby
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Battle;
