import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { RootState } from '../store';

const Characters: React.FC = () => {
  const navigate = useNavigate();
  const { characters } = useSelector((state: RootState) => state.game);
  const [selectedCharacter, setSelectedCharacter] = useState(characters[0]?.id);

  const currentCharacter = characters.find(char => char.id === selectedCharacter);

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      {/* Top Navigation */}
      <div className="bg-gray-800 p-4 flex justify-between items-center">
        <div className="flex items-center">
          <button 
            onClick={() => navigate('/')}
            className="mr-4 hover:text-gray-300"
          >
            <span className="material-icons">arrow_back</span>
          </button>
          <h1 className="text-2xl font-bold">Characters</h1>
        </div>
        <div className="flex items-center space-x-4">
          <button className="bg-blue-600 px-4 py-2 rounded-lg hover:bg-blue-700">
            Sort
          </button>
          <button className="bg-purple-600 px-4 py-2 rounded-lg hover:bg-purple-700">
            Filter
          </button>
        </div>
      </div>

      <div className="flex h-[calc(100vh-4rem)]">
        {/* Character List */}
        <div className="w-1/4 bg-gray-800 p-4 overflow-y-auto">
          <div className="space-y-2">
            {characters.map((character) => (
              <div
                key={character.id}
                onClick={() => setSelectedCharacter(character.id)}
                className={`
                  flex items-center p-3 rounded-lg cursor-pointer
                  ${selectedCharacter === character.id ? 'bg-blue-600' : 'bg-gray-700 hover:bg-gray-600'}
                  ${!character.isUnlocked ? 'opacity-50' : ''}
                `}
              >
                <div className="w-12 h-12 bg-gray-600 rounded-lg flex items-center justify-center mr-3">
                  <span className="material-icons">person</span>
                </div>
                <div className="flex-1">
                  <h3 className="font-bold">{character.name}</h3>
                  <p className="text-sm text-gray-400">Lv.{character.level}</p>
                </div>
                {!character.isUnlocked && (
                  character.isPaid ? (
                    <span className="material-icons text-blue-500">diamond</span>
                  ) : (
                    <span className="material-icons">lock</span>
                  )
                )}
              </div>
            ))}
          </div>
        </div>

        {/* Character Details */}
        {currentCharacter && (
          <div className="flex-1 p-6">
            <div className="grid grid-cols-3 gap-6">
              {/* Character Preview */}
              <div className="col-span-2">
                <div className="bg-gray-800 rounded-xl h-[400px] flex items-center justify-center mb-6 relative">
                  <span className="material-icons text-9xl">person</span>
                  {!currentCharacter.isUnlocked && (
                    <div className="absolute inset-0 bg-black bg-opacity-50 flex items-center justify-center flex-col">
                      {currentCharacter.isPaid ? (
                        <>
                          <span className="material-icons text-blue-500 text-6xl mb-4">diamond</span>
                          <p className="text-xl font-bold mb-2">Premium Character</p>
                          <button className="bg-blue-600 px-6 py-2 rounded-lg hover:bg-blue-700">
                            Purchase
                          </button>
                        </>
                      ) : (
                        <>
                          <span className="material-icons text-6xl mb-4">lock</span>
                          <p className="text-xl font-bold mb-2">Reach Level {currentCharacter.requiredLevel}</p>
                          <p className="text-gray-400">to unlock this character</p>
                        </>
                      )}
                    </div>
                  )}
                </div>
                <div className="grid grid-cols-4 gap-4">
                  {currentCharacter.skins.map((skin, index) => (
                    <div key={index} className="bg-gray-800 rounded-lg p-2 cursor-pointer hover:bg-gray-700">
                      <div className="aspect-square bg-gray-700 rounded flex items-center justify-center">
                        <span className="material-icons">style</span>
                      </div>
                      <p className="text-center text-sm mt-2">{skin}</p>
                    </div>
                  ))}
                </div>
              </div>

              {/* Stats and Abilities */}
              <div className="space-y-6">
                <div className="bg-gray-800 rounded-xl p-6">
                  <h2 className="text-xl font-bold mb-4">Stats</h2>
                  <div className="space-y-4">
                    <div>
                      <div className="flex justify-between text-sm mb-1">
                        <span>Health</span>
                        <span>{currentCharacter.health}/{currentCharacter.maxHealth}</span>
                      </div>
                      <div className="h-2 bg-gray-700 rounded-full overflow-hidden">
                        <div 
                          className="h-full bg-green-500" 
                          style={{ width: `${(currentCharacter.health / currentCharacter.maxHealth) * 100}%` }}
                        />
                      </div>
                    </div>
                    <div>
                      <div className="flex justify-between text-sm mb-1">
                        <span>Experience</span>
                        <span>{currentCharacter.experience}/1000</span>
                      </div>
                      <div className="h-2 bg-gray-700 rounded-full overflow-hidden">
                        <div 
                          className="h-full bg-blue-500" 
                          style={{ width: `${(currentCharacter.experience / 1000) * 100}%` }}
                        />
                      </div>
                    </div>
                  </div>
                </div>

                <div className="bg-gray-800 rounded-xl p-6">
                  <h2 className="text-xl font-bold mb-4">Abilities</h2>
                  <div className="space-y-4">
                    {currentCharacter.abilities.map((ability) => (
                      <div key={ability.id} className="bg-gray-700 rounded-lg p-4">
                        <div className="flex items-start justify-between mb-2">
                          <h3 className="font-bold">{ability.name}</h3>
                          {ability.cooldown > 0 && (
                            <span className="text-sm text-gray-400">CD: {ability.cooldown}s</span>
                          )}
                        </div>
                        <p className="text-sm text-gray-400 mb-2">{ability.description}</p>
                        {ability.damage > 0 && (
                          <div className="flex items-center text-yellow-500">
                            <span className="material-icons mr-1">flash_on</span>
                            <span>{ability.damage} DMG</span>
                          </div>
                        )}
                        {ability.duration && (
                          <div className="flex items-center text-blue-500">
                            <span className="material-icons mr-1">timer</span>
                            <span>{ability.duration}s</span>
                          </div>
                        )}
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Characters;
